import React, { useState } from 'react'
import Child from './Components/Child'

const Parent = () => {

    const country = [1, 3, 4, 5]

    const [store, setstore] = useState([])

    const [inputvalue, setinputvalue] = useState('')

    console.log("my store==>", store)
    // onClick={(e)=> setinputvalue(e.target.value)} 

    const additem = () => {

        setstore(() => {
            return [...store, inputvalue]

            
            
        })

        localStorage.setItem("todo",store)


        setinputvalue('')

    }

    const deletefunction = (index) =>{

        console.log("getting id==>",index)

         let ahmed= store.filter((a,id)=>{
            return  id !== index
         })

         setstore(ahmed)

       

        //  console.log("my store==>", store)

    }



    return (
        <>
            {/* <div className='d-flex' > */}
            <p>my Todo</p>

            <input type="text" value={inputvalue} onChange={(e) => setinputvalue(e.target.value)} />

            <button type="button" onClick={additem} >+</button>

            <ul>
                {
                    store.map((el,index) => {
                        return (
                            <>

                                <Child index={index} data={el}  del={deletefunction} />

                            </>
                        )
                    })


                }

            </ul>





        </>
    )
}

export default Parent
