import React, { useEffect, useState } from 'react'
import Child from './Components/Child'

const Parent = () => {

    const country = [1, 3, 4, 5]

    const [store, setstore] = useState([])

    const [inputvalue, setinputvalue] = useState('')
    const[errormsg,seterrormsg]=useState("")

    const [toglebtn,settoglebtn]=useState(false)

    const [edititem,setedititem]=useState(null)

    

    console.log("my store==>", store)
    // onClick={(e)=> setinputvalue(e.target.value)} 

    const additem = () => {

        if(inputvalue == "")
        {

            seterrormsg("Please Enter your Todo List ")


        }

        // else if ( !toglebtn) {

        //     setstore(()=>{
        //         store.map((el)=>{
        //             if()
        //         })
        //     })
         


        // }
        else{

            setstore(() => {
                return [...store, inputvalue]
    
    
    
            })
    
            localStorage.setItem("todo", [...store, inputvalue])
    
    
            setinputvalue('')

        }

       

    }

    const deletefunction = (index) => {

        console.log("getting id==>", index)

        let ahmed = store.filter((a, id) => {
            return id !== index
        })

        setstore(ahmed)

    }


    const eiditfunction = (a,b) =>{
 
       console.log("Edit==>",a,"btn",b)
        let editid=store.filter((data,index)=>{
            return a == index
        })
 
        settoglebtn(b)
        setinputvalue(editid)

        setedititem(editid)

   

        

    }

    console.log("edit item==>",edititem)

    // const edittodo = () =>{
    //     setstore(() => {
    //         return [...store, inputvalue]

    //     })

    //     localStorage.setItem("todo", [...store, inputvalue])


    //     setinputvalue('')

    // }



    return (
        <>
            {/* <div className='d-flex' > */}
            <div className='father' >

            
            <div className='form-todo' >

            
            <p>My Todo App</p>
            <p style={{color:"red"}} >{ 
            errormsg }
            </p>

            <input type="text" value={inputvalue}  onChange={(e) => setinputvalue(e.target.value)} />
              {
                toglebtn == true ?  
                <button type="button" className='plusbtn' onClick={additem}  >edit</button> : <button type="button" className='plusbtn' onClick={additem} >+</button>
              }
            

            <ul>
                {
                    store.map((el, index) => {
                        return (
                            <>

                                <Child index={index} data={el} btn={toglebtn} del={deletefunction} edit={eiditfunction} />

                            </>
                        )
                    })


                }

            </ul>

            </div>
           
            </div>




        </>
    )
}

export default Parent
