    var updatedList = [...checkedArray];
    if (event.target.checked) {
      updatedList = [...checkedArray, event.target.value];
    } else {
      updatedList.splice(checkedArray.indexOf(event.target.value), 1);
    }
    setChecked(updatedList);
  };

