import React, { useEffect, useState } from 'react'
import Child from './Components/Child'

const Parent = () => {

    const country = [1, 3, 4, 5]

    const [store, setstore] = useState([])

    const [inputvalue, setinputvalue] = useState('')
    const [errormsg, seterrormsg] = useState("")

    console.log("my store==>", store)
    // onClick={(e)=> setinputvalue(e.target.value)} 

    const additem = () => {

        if (inputvalue == "") {

            seterrormsg("Please Enter your Todo List ")


        }
        else {

            setstore(() => {
                return [...store, inputvalue]



            })

            localStorage.setItem("todo", [...store, inputvalue])


            setinputvalue('')

        }



    }

    const deletefunction = (index) => {

        console.log("getting id==>", index)

        let ahmed = store.filter((a, id) => {
            return id !== index
        })

        setstore(ahmed)

    }

    //     useEffect(() => {
    // something()


    //     }, [])

    //  const something = (event) => {
    //         if (event.keyCode === 13) {
    //             console.log('enter')
    //         }


    //     }


    return (
        <>
            {/* <div className='d-flex' > */}
            <div className='father' >


                <div className='form-todo' >


                    <p>My Todo App</p>
                    <p style={{ color: "red" }} >{
                        errormsg}
                    </p>

                    <input type="text" value={inputvalue} onChange={(e) => setinputvalue(e.target.value)} />

                    <button type="button" className='plusbtn' onClick={additem} >+</button>
                    <div className='store-container' >
                        <ul>
                            {
                                store.map((el, index) => {
                                    return (
                                        <>

                                            <Child index={index} data={el} del={deletefunction} />

                                        </>
                                    )
                                })


                            }

                        </ul>

                    </div>

                </div>

            </div>




        </>
    )
}

export default Parent
